        /// The [OCID](https://docs.cloud.oracle.com/iaas/Content/General/Concepts/identifiers.htm) of the VM cluster.
        /// </summary>
        [Input("vmClusterId")]
        public Input<string>? VmClusterId { get; set; }

        public DatabaseSnapshotStandbyState()
        {
        }
        public static new DatabaseSnapshotStandbyState Empty => new DatabaseSnapshotStandbyState();
    }
